import React, { Component } from "react";
import { Card } from "antd";
import { Chart, Geom, Axis, Tooltip, Legend } from "bizcharts";
import "./home.less";

/* 首页路由 */
export default class Home extends Component {
  state = {
    key: "tab1",
    noTitleKey: "article",
  };

  render() {
    const tabListNoTitle = [
      {
        key: "article",
        tab: "访问量",
      },
      {
        key: "app",
        tab: "销售量",
      },
    ];
    const contentListNoTitle = {
      article: <p>article content</p>,
      app: <p>app content</p>,
    };
    const data = [
      {
        month: "Jan",
        city: "Tokyo",
        temperature: 7,
      },
      {
        month: "Jan",
        city: "London",
        temperature: 3.9,
      },
      {
        month: "Feb",
        city: "Tokyo",
        temperature: 6.9,
      },
      {
        month: "Feb",
        city: "London",
        temperature: 4.2,
      },
      {
        month: "Mar",
        city: "Tokyo",
        temperature: 9.5,
      },
      {
        month: "Mar",
        city: "London",
        temperature: 5.7,
      },
      {
        month: "Apr",
        city: "Tokyo",
        temperature: 14.5,
      },
      {
        month: "Apr",
        city: "London",
        temperature: 8.5,
      },
      {
        month: "May",
        city: "Tokyo",
        temperature: 18.4,
      },
      {
        month: "May",
        city: "London",
        temperature: 11.9,
      },
      {
        month: "Jun",
        city: "Tokyo",
        temperature: 21.5,
      },
      {
        month: "Jun",
        city: "London",
        temperature: 15.2,
      },
      {
        month: "Jul",
        city: "Tokyo",
        temperature: 25.2,
      },
      {
        month: "Jul",
        city: "London",
        temperature: 17,
      },
      {
        month: "Aug",
        city: "Tokyo",
        temperature: 26.5,
      },
      {
        month: "Aug",
        city: "London",
        temperature: 16.6,
      },
      {
        month: "Sep",
        city: "Tokyo",
        temperature: 23.3,
      },
      {
        month: "Sep",
        city: "London",
        temperature: 14.2,
      },
      {
        month: "Oct",
        city: "Tokyo",
        temperature: 18.3,
      },
      {
        month: "Oct",
        city: "London",
        temperature: 10.3,
      },
      {
        month: "Nov",
        city: "Tokyo",
        temperature: 13.9,
      },
      {
        month: "Nov",
        city: "London",
        temperature: 6.6,
      },
      {
        month: "Dec",
        city: "Tokyo",
        temperature: 9.6,
      },
      {
        month: "Dec",
        city: "London",
        temperature: 4.8,
      },
    ];
    const cols = {
      month: {
        range: [0, 1],
      },
    };
    return (
      <div>
        <div className="div-card-chart">
          <Card title="商品总量" className="card-product">
            <p>Card content</p>
            <p>Card content</p>
            <p>Card content</p>
          </Card>
          <Chart height={250} width={800} className="chart-line" data={data} scale={cols} autoFit>
            <Legend
              marker={{
                symbol: (x, y, radius) => {
                  const r = radius / 2;
                  return [
                    ["M", x - radius, y],
                    ["A", r, r, 0, 0, 1, x, y],
                    ["A", r, r, 0, 0, 0, x + radius, y],
                  ];
                },
                style: {
                  fill: "",
                },
              }}
            />
            <Axis name="month" />
            <Axis
              name="temperature"
              label={{
                formatter: (val) => `${val}°C`,
              }}
            />
            <Tooltip
              domStyles={{
                "g2-tooltip": {
                  boxShadow: "none",
                  color: "#fff",
                  backgroundColor: "#222",
                },
              }}
              crosshairs={{
                type: "y",
              }}
              style={{
                color: "red",
              }}
            />
            <Geom
              type="line"
              position="month*temperature"
              size={2}
              color={"city"}
            />
            <Geom
              type="point"
              position="month*temperature"
              size={4}
              shape={"circle"}
              color={"city"}
              style={{
                stroke: "#fff",
                lineWidth: 1,
              }}
            />
          </Chart>
        </div>
        <Card
          className="div-card2"
          tabList={tabListNoTitle}
          activeTabKey={this.state.noTitleKey}
          onTabChange={(key) => {
            this.onTabChange(key, "noTitleKey");
          }}
        >
          <Card>

          </Card>
        </Card>
      </div>
    );
  }
}
